import { useState } from "react";

import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import StarIcon from "@mui/icons-material/Star";

function Testimonials() {
  const [expandedReviews, setExpandedReviews] = useState({}); // Track read more states

  let details = [
    {
      name: "Pavithra",
      img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTOG5TM1EovYcHRS_Uoi7lufuMrQ3slzfmoLg&s",
      date: "2024-09-12",
      review:"The restaurant is very attractive. And I got a very unique experience from there excellent food preparations. I had ordered Chinese dishes and the test of the dish was very haiglable. And also the staffs behavior was very nice. I will sure visit there again and again."
    },
    {
      name: "Arun",
      img: "data:image/jpeg;base64,",
      date: "2024-09-14",
      review:
        "The food court was a pleasant surprise! The Sandwich from Libi'o was surprisingly delicious, with fresh ingredients and great flavor - definitely a standout option for a quick and tasty meal.",
    },
    {
      name: "Rohith",
      img: "data:image/jpeg;base64,",
      date: "2024-09-16",
      review:
        "The 'Chicken POPS' at the Asian Fusion stall was fantastic! Perfectly cooked rice, tender chicken, and a flavorful sauce - a great lunch choice." ,
    },
    {
      name: "Anees",
      img: "data:image/jpeg;base64,",
      date: "2024-09-18",
      review:
        "The pizza at the Italian place was a bit underwhelming, but the garlic knots were amazing - definitely worth a try if you're looking for a quick bite",
    },
  ];

  const settings = {
    arrows: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 3,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    responsive: [
      { breakpoint: 1024, settings: { slidesToShow: 2 } },
      { breakpoint: 640, settings: { slidesToShow: 2 } },
      { breakpoint: 580, settings: { slidesToShow: 1, centerMode: true } },
    ],
  };

  return (
    <div className="container  xxl:max-w-[65vw] xxl:min-h-[30vh] w-[100vw] py-10 px-10  mx-auto overflow-hidden">

      <h1 className="text-2xl font-bold text-center pb-[20px]">Our Customers Reviews</h1>

      <Slider className="pl-3" {...settings}>
        {details.map((detail, index) => (
          <div
            key={index}
            className="rounded-2xl bg-[#F4F4F4] border-2 border-red-500 px-3 pt-1 pb-3"
          >
            <div className="flex flex-row items-center gap-3  py-3">
              <div >
                <img
                  className="w-9 h-9 rounded-full  "
                  src={detail.img}
                  alt="User"
                />
              </div>
              <div>
                <h2 className="text-base font-bold">{detail.name}</h2>
                <p className="text-xs font-light">{detail.date}</p>
              </div>
            </div>
            <div className="flex flex-col">
              <div className="flex">
                {[...Array(5)].map((_, i) => (
                  <StarIcon
                    key={i}
                    sx={{ fontSize: 20 }}
                    className="text-yellow-500"
                  />
                ))}
              </div>
              <div
                className={detail.review.length < 100 ? " h-[80px]" : "h-auto"}
              >
                <p
                  className={`font-sans text-sm  ${
                    expandedReviews[index]
                      ? " h-auto"
                      : "h-[60px] max-h-[60px] overflow-hidden"
                  } }`}
                >
                  {detail.review}
                </p>
                {detail.review.length > 100 && (
                  <button
                    className="underline mt-1 text-sm flex justify-start"
                    onClick={() =>
                      setExpandedReviews((prev) => ({
                        ...prev,
                        [index]: !prev[index],
                      }))
                    }
                  >
                    {expandedReviews[index] ? "Read Less" : "Read More"}
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default Testimonials;
